import React, { useState } from 'react';
import Head from "next/head";

import { Container, Jumbotron, Form, FormGroup, Label, Input, Button } from "reactstrap";

import Menu from "../components/Menu";
import Rodape from "../components/Rodape";

function Home() {

  const [contato, setContato] = useState({
    name: "",
    email: "",
    subject: "",
    content: ""

  });

  const [response, setResponse] = useState({
    formSave: false,
    type: '',
    message: ''
  });

  const onChangeInput = e => setContato({ ...contato, [e.target.name]: e.target.value });

  const sendMsg = async e => {
    e.preventDefault();

    setResponse({formSave: true})
    try {
      const res = await fetch("http://localhost:8080/contato", {
        method: "POST",
        body: JSON.stringify(contato),
        headers: { "Content-Type": "application/json" }
      });

      const responseEnv = await res.json();
      if (responseEnv.error) {
        setResponse({
          formSave: false,
          type: 'error',
          message: responseEnv.message
        })
      } else {
        setResponse({
          formSave: false,
          type: 'success',
          message: responseEnv.message
        })
      }
    } catch (err) {
      setResponse({
        formSave: false,
        type: 'error',
        message: 'Error: Mensagem não enviada com sucesso, tente mais tarde!'
      })
    }
  }
    return (
      <>
        <Head>
          <title>Contato - Celke</title>
          <meta name="description" content="Contato com a empresa..." />
        </Head>

        <Menu />

        <Jumbotron fluid className="descr-top">
          <style>
            {`.descr-top{
              background-color: #000;
              color: #fed136;
              padding-top: 100px;
              padding-bottom: 50;
              margin-bottom: 0rem !important;

            }
            `}
          </style>
          <Container className="text-center">
            <h1 className="display-4">Contato</h1>
          </Container>
        </Jumbotron>

        <Jumbotron fluid className="form-contato">
          <style>
            {`.form-contato{
              padding-top: 80px;
              padding-bottom: 80px;
              background-color: #fff;
              margin-bottom: 0rem !important
            }`}
          </style>
          <Container>
            {response,type}
            <Form onSubmit={sendMsg}>
              <FormGroup>
                <Label for="name">Nome</Label>
                <Input
                  type="text"
                  name="name"
                  id="name"
                  placeholder="Nome completo"
                  onChangeInput={onChangeInput}
                />
              </FormGroup>

              <FormGroup>
                <Label for="email">E-mail</Label>
                <Input
                  type="email"
                  name="email"
                  id="email"
                  placeholder="Melhor e-mail"
                  onChangeInput={onChangeInput}
                />
              </FormGroup>

              <FormGroup>
                <Label for="subject">Assunto</Label>
                <Input
                  type="text"
                  name="subject"
                  id="subject"
                  placeholder="Assunto da mensagem"
                  onChangeInput={onChangeInput}
                />
              </FormGroup>

              <FormGroup>
                <Label for="content">Conteúdo</Label>
                <Input
                  type="text"
                  name="content"
                  id="content"
                  placeholder="Conteúdo da mensagem"
                  onChangeInput={onChangeInput}
                />
              </FormGroup>

              <Button type="submit" outline color="warning">
                Enviar
              </Button>
            </Form>
          </Container>
        </Jumbotron>

        <Rodape />
      </>
    );
  };

  export default Home;
